import React from "react";
import IndexBar from "./IndexBar";
import CalcButton from "./CalcButton";

import CalcButtons from "./CalcButtons";

class Display extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      index: "",
      toCalc: "",
      result: "Sequence Result",
      stamp: "Time Stamp"
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ index: event.target.value });
    console.log("onChange: ", event.target.value);
  }

  loopWay = () => {
    console.log("index: ", this.state.index);
    var fiboNew;
    var fiboPrev = 1;
    var fiboFirst = 0;

    if (this.state.index <= 1 && this.state.index >= 0) return this.state.index;
    for (let sequence = 2; sequence <= this.state.index; sequence++) {
      fiboNew = fiboFirst + fiboPrev;
      fiboFirst = fiboPrev;
      fiboPrev = fiboNew;
    }
    //let seq = fiboNew;
    this.setState({ result: fiboNew });
    console.log("Loop Way Calculation", fiboNew);
  };

  render() {
    return (
      <div className="index">
        <IndexBar input={this.state.index} handleChange={this.handleChange} />
        <span className="result">{this.state.result}</span>
        <span className="stamp">{this.state.stamp}</span>
        {/* <CalcButtons 
               toCalc={this.state.index} 
               result={this.handleResult}
               stamp={this.state.stamp}
            /> */}
        <div>
          <CalcButton
            name={"Loop Way"}
            variant={"success"}
            clicked={this.loopWay}
          />
          <CalcButton
            name={"Recursion Way"}
            variant={"success"}
            onClick={this.function_RecursionWay}
          />
          <CalcButton
            name={"Reduce Way"}
            variant={"success"}
            onClick={this.function_ReduceWay}
          />
          <CalcButton
            name={"Reset"}
            variant={"warning"}
            onClick={this.function_reset}
          />
        </div>
      </div>
    );
  }
}

export default Display;
